import * as React from 'react';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import { io } from "socket.io-client";

export default function BasicList(props) {

    const [choosenFile, setChoosenFile] = React.useState('')
    const socket = io()
    const files = []

    useEffect(() => {
        socket.on('setFiles', msg => {
            files = msg
        })
    })

    return (

        <List style={{ margin: 'auto', width: '50%', height: '100%', paddingTop: '30px' }}>

            {props.files.map((element, index) => {
                return (

                    <ListItem disablePadding style={{ paddingBottom: '5px', marginTop: index * 5 + 'px' }}>
                        <ListItemButton style={{ border: '2px solid #292929', borderRadius: '10px' }} onClick={() => setChoosenFile(element.fileName)}>
                            <ListItemText primary={element.fileName} secondary={element.fileInfo} />
                        </ListItemButton>
                    </ListItem>

                )
            })}

        </List>

    )
}
