import React from 'react';
import './App.css';
import BasicList from './BasicList';
import Button from '@mui/material/Button';
import { useFilePicker } from 'use-file-picker';
import axios from 'axios';

function App() {

  const childRef = React.createRef();
  //const [page, setPage] = React.useState(0)
  const [openFileSelector, { filesContent }] = useFilePicker({
    accept: ['.pdf', '.html'],
    multiple: true,
  });

  const upload = (e) => {
    filesContent.map(file => (
      uploadFiles(e, file)
    ))
  }

  const uploadFiles = async (e, file) => {

    e.preventDefault()

    let formData = new FormData()
    formData.append('file', file)

    await axios.post('http://localhost:3001/upload', formData)
      .then(res => {
        console.log(res)
      })
      .catch(error => {
        console.log(error)
      });
  }

  const remove = () => {

  }

  return (

    <div style={{ width: '100vw', height: '100vh', backgroundColor: '#d3d3d3', overflow: 'hidden' }}>

      <div style={{ margin: 'auto', width: '50%', paddingTop: '50px', textAlign: 'center', color: '#292929', fontSize: '35px' }}>File Manager</div>

      <center style={{ paddingTop: '20px' }}>
        <Button variant="contained" color='success' onClick={openFileSelector} style={{ marginRight: '10px' }}>Select</Button>
        <Button variant="contained" color='success' onClick={upload} style={{ marginRight: '10px' }}>Upload</Button>
        <Button variant="contained" color='error' onClick={remove}>Delete</Button>
      </center>

      {/*<BasicList ref={childRef} />*/}

    </div>

  )

}

export default App;
