import React from 'react';
import './App.css';
import BasicList from './BasicList';
import Button from '@mui/material/Button';
import axios from 'axios';
import { Input } from '@mui/material';
/*import { io } from "socket.io-client";
import { useEffect } from 'react'*/

function App() {

  let files = []

  const [selectedFile, setSelectedFile] = React.useState(null)

  const onFileChange = event => setSelectedFile(event.target.files[0])

  const onFileUpload = () => {
    const formData = new FormData();
    formData.append('file', selectedFile)
    axios.post('http://localhost:3001/upload', formData).then(result => {
      files = result.data
      console.log(files)
    })
  }

  /*const socket = new io()
  let files = []
  useEffect(() => socket.emit('getFiles'))
  socket.on('setFiles', msg => files = msg)*/

  return (

    <div style={{ width: '100vw', height: '100vh', backgroundColor: '#d3d3d3', overflow: 'hidden' }}>

      <div style={{ margin: 'auto', width: '50%', paddingTop: '50px', textAlign: 'center', color: '#292929', fontSize: '35px' }}>File Manager</div>

      <center style={{ paddingTop: '20px' }}>
        <Input type="file" onChange={onFileChange} />
        <Button variant="contained" color='success' onClick={onFileUpload} style={{ marginRight: '10px', marginLeft: '10px' }}>Upload</Button>
        <Button variant="contained" color='error' >Delete</Button>
      </center>

      <BasicList files={[]} />

    </div>

  )

}

export default App;
