import React from 'react';
import './App.css';
import BasicList from './BasicList';
import Button from '@mui/material/Button';
import { useFilePicker } from 'use-file-picker';
import axios from 'axios';
import { Input } from '@mui/material';

function App() {

  const childRef = React.createRef();
  //const [page, setPage] = React.useState(0)
  const [selectedFile, setSelectedFile] = React.useState(null)
  /*const [openFileSelector, { filesContent }] = useFilePicker({
    accept: ['.pdf', '.html'],
    multiple: true,
  });

  const upload = (e) => {
    filesContent.map(file => (
      uploadFiles(e, file)
    ))
  }

  const uploadFiles = async (e, file) => {

    e.preventDefault()

    let formData = new FormData()
    formData.append('file', file)

    const config = {
      headers: {
        'content-type': 'multipart/form-data',

      }
    }

    axios.post("/upload", formData, config)
      .then(res => {
        console.log(res)
      })
      .catch(error => {
        console.log(error)
      });
  }*/

  const onFileChange = event => { setSelectedFile({ selectedFile: event.target.files[0] }) }

  const onFileUpload = () => {
    const formData = new FormData();
    formData.append("myFile", selectedFile, selectedFile.name)
    console.log(selectedFile);
    axios.post("api/uploadfile", formData);
  }

  return (

    <div style={{ width: '100vw', height: '100vh', backgroundColor: '#d3d3d3', overflow: 'hidden' }}>

      <div style={{ margin: 'auto', width: '50%', paddingTop: '50px', textAlign: 'center', color: '#292929', fontSize: '35px' }}>File Manager</div>

      <center style={{ paddingTop: '20px' }}>
        <input type="file" onChange={onFileChange} />
        <Button variant="contained" color='success' onClick={onFileUpload} style={{ marginRight: '10px' }}>Upload</Button>
        <Button variant="contained" color='error' >Delete</Button>
      </center>

      {/*<BasicList ref={childRef} />*/}

    </div>

  )

}

export default App;
